import React, { useState, useEffect } from "react";
import SelectSexo from "./SelectSexo";
import SelectRoles from "./SelectRoles";
import { Check } from "lucide-react";

function FormularioUsuario() {
  const empelado = JSON.parse(localStorage.getItem("empleado"));

  const [values, setValues] = useState({
    nombre: "",
    ap: "",
    am: "",
    sexo: "",
    telefono: "",
    direccion: "",
    rfc: "",
    password: "",
    rol: "",
    gerenteAlta: empelado.usu_id,
  });

  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <form action="" onSubmit={handleSubmit}>
        {/*  INPUT NOMBRE */}
        <div>
          <p>Nombre</p>
          <input
            type="text"
            name="nombre"
            placeholder="Nombre de Usuario"
            onChange={handleInput}
          />
        </div>

        {/*  INPUT APELLIDO PATERNO */}
        <div>
          <p>Apellido Paterno</p>
          <input
            type="text"
            name="ap"
            placeholder="Apellido Paterno"
            onChange={handleInput}
          />
        </div>

        {/*  INPUT APELLIDO MATERNO */}
        <div>
          <p>Apellido Materno</p>
          <input
            type="text"
            name="am"
            placeholder="Apellido Materno"
            onChange={handleInput}
          />
        </div>

        {/*  INPUT SEXO */}
        <div>
          <SelectSexo onChange={handleInput} />
        </div>

        {/*  INPUT TELEFONO */}
        <div>
          <p>Numero de Telefono</p>
          <input type="text" name="telefono" onChange={handleInput} />
        </div>

        {/*  INPUT DIRECCION */}
        <div>
          <p>Direccion</p>
          <input type="text" name="direccion" onChange={handleInput} />
        </div>

        {/*  INPUT RFC */}
        <div>
          <p>RFC</p>
          <input type="text" name="rfc" onChange={handleInput} />
        </div>
        {/*  INPUT PASSWORD */}
        <div>
          <p>Contraseña</p>
          <input type="text" name="password" onChange={handleInput} />
        </div>

        {/*  INPUT ROL */}
        <div>
          <p>Rol de Empleado</p>
          <SelectRoles onChange={handleInput} />
        </div>
        <button
          className="bg-blue-500 flex justify-center items-center rounded-xl py-1.5 px-8 font-medium text-white gap-2"
          type="submit"
        >
          {<Check className="w-6 h-6" />}
          Agregar Usuario
        </button>
      </form>
    </div>
  );
}

export default FormularioUsuario;
