import React, { useState, useEffect } from "react";
import SelectSexo from "./SelectSexo";
import SelectRoles from "./SelectRoles";

function FormularioUsuario() {
  const empelado = JSON.parse(localStorage.getItem("empleado"));

  const [values, setValues] = useState({
    nombre: "",
    ap: "",
    am: "",
    sexo: "",
    telefono: "",
    direccion: "",
    rfc: "",
    password: "",
    rol: "",
    gerenteAlta: empelado.usu_id,
  });

  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  return (
    <div>
      <form action="">
        {/*  INPUT NOMBRE */}
        <div>
          <p>Nombre</p>
          <input type="text" name="nombre" placeholder="Nombre de Usuario" />
        </div>

        {/*  INPUT APELLIDO PATERNO */}
        <div>
          <p>Apellido Paterno</p>
          <input type="text" name="ap" placeholder="Apellido Paterno" />
        </div>

        {/*  INPUT APELLIDO MATERNO */}
        <div>
          <p>Apellido Materno</p>
          <input type="text" name="am" placeholder="Apellido Materno" />
        </div>

        {/*  INPUT SEXO */}
        <div>
          <SelectSexo />
        </div>

        {/*  INPUT TELEFONO */}
        <div>
          <p>Numero de Telefono</p>
          <input type="text" name="telefono" />
        </div>

        {/*  INPUT DIRECCION */}
        <div>
          <p>Direccion</p>
          <input type="text" name="direccion" />
        </div>

        {/*  INPUT RFC */}
        <div>
          <p>RFC</p>
          <input type="text" name="rfc" />
        </div>
        {/*  INPUT PASSWORD */}
        <div>
          <p>Contrase√±a</p>
          <input type="text" name="password" />
        </div>

        {/*  INPUT ROL */}
        <div>
          <p>Rol de Empleado</p>
          <SelectRoles />
        </div>
      </form>
    </div>
  );
}

export default FormularioUsuario;
