import React, { useEffect, useState } from "react";
import axios from "axios";

function Tablausuarios() {
  function parseRol(rol) {
    if (rol === "g") {
      return "Gerente";
    }
    if (rol === "e") {
      return "Empleado de Mostrador";
    }
    if (rol === "i") {
      return "Gestor de inventario";
    }
    return undefined;
  }

  const [data, setData] = useState([]);
  useEffect(() => {
    axios
      .post("http://localhost:8081/usuarios")
      .then((res) => setData(res.data))
      .catch((err) => console.log("hubo un error"));
  }, []);
  return (
    <div className="container">
      <div className="mt-3">
        <table className="table">
          <thead>
            <th>ID</th>
            <th>NOMBRE</th>
            <th>ROL</th>
          </thead>
          <tbody>
            {data.map((user, index) => {
              return (
                <tr key={index}>
                  <td>{user.usu_id}</td>
                  <td>{user.usu_nombre}</td>
                  <td>{user.usu_rol}</td>
                  <td>
                    {<a href={`/usuario?${parseRol(user.usu_id)}`}>Editar</a>}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tablausuarios;
