import React, { useState, useEffect } from "react";
import axios from "axios";
import { Pencil } from "lucide-react";

function TablaProveedor() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .post("http://localhost:8081/proveedores")
      .then((res) => setData(res.data))
      .catch((err) => {
        console.log("Hubo un error");
      });
  }, []);
  return (
    <div className="container">
      <div className="mt-3">
        <table className="table">
          <thead>
            <th>ID</th>
            <th>NOMBRE</th>
            <th>CORREO</th>
            <th>TELEFONO</th>
            <th>DIRECCION</th>
            <th>FECHA ALTA</th>
          </thead>
          <tbody>
            {data.map((proveedor, index) => {
              return (
                <tr key={index}>
                  <td>{proveedor.prove_id}</td>
                  <td>{proveedor.prove_nombre}</td>
                  <td>{proveedor.prove_correo}</td>
                  <td>{proveedor.prove_telefono}</td>
                  <td>{proveedor.prove_direccion}</td>
                  <td>{proveedor.prove_fechaAlta}</td>
                  <td>
                    {
                      <a
                        href={`/proveedor?${proveedor.prove_id}`}
                        className="hover:underline text-blue-400"
                      >
                        {" "}
                        Editar{" "}
                      </a>
                    }
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default TablaProveedor;
