import React, { useState, useEffect } from "react";
import axios from "axios";
import { Check } from "lucide-react";

function Agregarproducto() {
  function vaciarInputs() {
    const inputs = document.querySelectorAll("input");
    inputs.forEach((input) => {
      input.value = "";
    });
  }

  const [values, setValues] = useState({
    nombre: "",
    categoria: "",
    precio: "",
    stock: "",
    codigo: "",
    marca: "",
    descripcion: "",
    stockMinimo: "",
  });

  const [categoria, setCategoria] = useState([]);

  useEffect(() => {
    axios
      .post("http://localhost:8081/categorias")
      .then((res) => setCategoria(res.data))
      .catch((err) => {
        console.log("Hubo un error");
      });
  }, []);

  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:8081/productos/agregar", values)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => console.log(err));
    vaciarInputs();
  };

  return (
    <div className="w-full  ">
      <form className="" action="" onSubmit={handleSubmit}>
        {/*MENSAJE Y BOTON AGREGAR*/}
        <div className=" flex justify-between items-center py-8 px-3">
          <p className=" flex-col justify-center items-center font-normal text-lg ">
            Agregar Nuevo Producto
          </p>
          <button
            className="bg-blue-500 flex justify-center items-center rounded-xl py-2.5 px-8 font-medium text-white gap-2"
            type="submit"
          >
            {<Check className="w-6 h-6" />}
            Agregar Producto
          </button>
        </div>
        <div className="grid grid-flow-row grid-rows-5 space-y-8">
          {/*PRIMERA COLUMNA */}
          <div className="grid grid-flow-col grid-cols-11 gap-8 px-4 row-span-3">
            {/* INFORMACION GENERAL*/}
            <div className="flex-col bg-zinc-200 col-span-6 rounded-xl p-3 space-y-3">
              <p className="font-semibold text-base">Informacion General</p>
              {/* INPUT NOMBRE*/}
              <div className="flex-col space-y-1.5">
                <p className="font-normal text-zinc-600 text-sm">
                  Nombre del producto
                </p>
                <input
                  className="bg-zinc-700 w-full   rounded-md px-2 py-1 focus:ring-blue-500 focus:border-blue-500 "
                  type="text"
                  placeholder="Nombre Producto"
                  name="nombre"
                  onChange={handleInput}
                />
              </div>
              {/* INPUT DESCRIPCION*/}
              <div className="flex-col space-y-1.5">
                <p className="font-medium text-zinc-600 text-sm">Descripcion</p>
                <input
                  className="bg-zinc-700 w-full h-32 rounded-md px-2 py-1 text-start"
                  type="text"
                  placeholder="Carro rojo"
                  name="descripcion"
                  onChange={handleInput}
                />
              </div>
              {/* INPUT MARCA*/}
              <div className="flex-col space-y-2">
                <p className="font-medium text-zinc-600 text-sm">Marca</p>
                <input
                  className="bg-zinc-700 rounded-md px-2 py-1 w-1/2"
                  type="text"
                  placeholder="Apple"
                  name="marca"
                  onChange={handleInput}
                />
              </div>
            </div>

            {/* IMAGEN*/}
            <div className=" bg-zinc-200 col-span-5 rounded-xl">
              <div className="p-3">
                <p className="font-medium">Agregar Imagenes</p>
              </div>
            </div>
          </div>

          {/*SEGUNDA COLUMNA */}
          <div className="grid grid-flow-col grid-cols-11 gap-8 px-4 row-span-2">
            <div className="bg-zinc-200 col-span-6 p-4 rounded-lg">
              <div className="grid grid-cols-2 gap-4">
                {" "}
                {/* INPUT PRECIO*/}
                <div className="flex-col space-y-2">
                  <p className="font-medium text-zinc-600 text-sm">Precio</p>
                  <input
                    className="bg-zinc-700 rounded-md px-2 py-1 w-full"
                    type="text"
                    placeholder="200.99"
                    name="precio"
                    onChange={handleInput}
                  />
                </div>
                {/* INPUT STOCK*/}
                <div className="flex-col space-y-2">
                  <p className="font-medium text-zinc-600 text-sm">Stock</p>
                  <input
                    className="bg-zinc-700 rounded-md px-2 py-1"
                    type="number"
                    placeholder="5"
                    name="stock"
                    onChange={handleInput}
                  />
                </div>
                {/* INPUT STOCK MINIMO*/}
                <div className="flex-col space-y-2">
                  <p className="font-medium text-zinc-600 text-sm">
                    Stock mínimo
                  </p>
                  <input
                    className="bg-zinc-700 rounded-md px-2 py-1"
                    type="number"
                    placeholder="2"
                    name="stockMinimo"
                    onChange={handleInput}
                  />
                </div>
                {/* INPUT DESCUENTO*/}
                <div className="flex-col space-y-2">
                  <p className="font-medium text-zinc-600 text-sm">Descuento</p>
                  <input
                    className="bg-zinc-700 rounded-md px-2 py-1"
                    type="number"
                    placeholder="2"
                    name="stockMinimo"
                    onChange={handleInput}
                  />
                </div>
              </div>
            </div>
            <div className="bg-zinc-200 col-span-5 p-4 rounded-lg">
              {/* INPUT CATEGORIA*/}
              <div className="flex-col space-y-2">
                <p className="font-medium text-zinc-600 text-sm">Categoria</p>
                <select name="categoria" onChange={handleInput}>
                  {categoria.map((categoria) => (
                    <option value={categoria.cat_id}>
                      {categoria.cat_nombre}
                    </option>
                  ))}
                </select>
              </div>

              {/* INPUT CODIGO*/}
              <div className="flex-col space-y-2">
                <p className="font-medium text-zinc-600 text-sm">Código</p>
                <input
                  className="bg-zinc-700 rounded-md px-2 py-1"
                  type="text"
                  placeholder="Código"
                  name="codigo"
                  onChange={handleInput}
                />
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
}

export default Agregarproducto;
