import React, { useEffect, useState } from "react";
import axios from "axios";
import Productcard from "./componentes/Productcard";
import { Plus } from "lucide-react";

function Productos() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .post("http://localhost:8081/productos")
      .then((res) => setData(res.data))
      .catch((err) => console.log("hubo un error"));
  }, []);
  return (
    <div>
      <a href="/proveedor/agregar " className="pr-6">
        <div className="bg-blue-600 px-4 py-2 rounded-md flex justify-center items-center space-x-3 ">
          <Plus className="w-6 h-6 text-white" />
          <p className="text-white font-semibold">Agregar proveedor</p>
        </div>
      </a>
      Productos
      <div className="flex gap-5">
        {data.map((producto, index) => {
          return (
            <Productcard
              key={index}
              nombre={producto.pro_nombre}
              precio={producto.pro_precio}
              stock={producto.pro_stock}
              id={producto.pro_id}
            />
          );
        })}
      </div>
    </div>
  );
}

export default Productos;
