import React, { useEffect, useState } from "react";
import axios from "axios";

function Tablausuarios() {
  function parseRol(rol) {
    if (rol === "g") return "Gerente";
    if (rol === "e") return "Empleado de Mostrador";
    if (rol === "i") return "Gestor de Inventario";
    return "Desconocido";
  }

  const [data, setData] = useState([]);
  useEffect(() => {
    axios
      .post("http://localhost:8081/usuarios")
      .then((res) => setData(res.data))
      .catch((err) => console.log("hubo un error"));
  }, []);

  return (
    <div className="container">
      <div className="mt-2">
        <table className="table-auto">
          <thead className="text-slate-500 font-normal text-xs bg-blue-100">
            <th>NOMBRE</th>
            <th>SEXO</th>
            <th>TELEFONO</th>
            <th>DIRECCION</th>
            <th>RFC</th>
            <th>ROL</th>
            <th>GERENTE ALTA</th>
          </thead>
          <tbody>
            {data.map((user, index) => {
              return (
                <tr key={index}>
                  <td>{user.usu_nombre}</td>
                  <td>{user.usu_nombre}</td>
                  <td>{user.usu_nombre}</td>
                  <td>{user.usu_nombre}</td>
                  <td>{user.usu_nombre}</td>

                  <td>{parseRol(user.usu_rol)}</td>
                  <td>{<a href={`/usuario?${user.usu_id}`}>Editar</a>}</td>
                  <td>{user.usu_nombre}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tablausuarios;
